mapper.select:
SELECT


,yingdi.yingdi_name as yingdiName
,yingdi.yingdi_uuid_number as yingdiUuidNumber
,yingdi.yingdi_photo as yingdiPhoto
,yingdi.yingdi_address as yingdiAddress
,yingdi.yingdi_types as yingdiTypes
,yingdi.yingdi_new_money as yingdiNewMoney
,yingdi.yingdi_zhuangtai_types as yingdiZhuangtaiTypes
,yingdi.yingdi_clicknum as yingdiClicknum
,yingdi.yingdi_content as yingdiContent
,yingdi.yingdi_delete as yingdiDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM yingdi_order  a
left JOIN yingdi yingdi ON a.yingdi_id = yingdi.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.yingdi_id = #{params.yingdiId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and UNIX_TIMESTAMP(a.yingdi_order_time) >= UNIX_TIMESTAMP(#{params.yingdiOrderTimeStart}) 


          and UNIX_TIMESTAMP(a.yingdi_order_time) <= UNIX_TIMESTAMP(#{params.yingdiOrderTimeEnd}) 


          and a.yingdi_order_true_price >= #{params.yingdiOrderTruePriceStart}   


          and a.yingdi_order_true_price <= #{params.yingdiOrderTruePriceEnd}   


        and a.yingdi_order_types = #{params.yingdiOrderTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.yingdi_id IS NOT NULL


        and yingdi.yingdi_name like CONCAT('%',#{params.yingdiName},'%')


        and yingdi.yingdi_uuid_number like CONCAT('%',#{params.yingdiUuidNumber},'%')


        and yingdi.yingdi_address like CONCAT('%',#{params.yingdiAddress},'%')


        and yingdi.yingdi_types = #{params.yingdiTypes}



          and yingdi.yingdi_new_money >= #{params.yingdiNewMoneyStart}   


          and yingdi.yingdi_new_money <= #{params.yingdiNewMoneyEnd}   


        and yingdi.yingdi_zhuangtai_types = #{params.yingdiZhuangtaiTypes}



          and yingdi.yingdi_clicknum >= #{params.yingdiClicknumStart}   


          and yingdi.yingdi_clicknum <= #{params.yingdiClicknumEnd}   


        and yingdi.yingdi_clicknum = #{params.yingdiClicknum}


        and yingdi.yingdi_content like CONCAT('%',#{params.yingdiContent},'%')


          and yingdi.yingdi_delete >= #{params.yingdiDeleteStart}   


          and yingdi.yingdi_delete <= #{params.yingdiDeleteEnd}   


        and yingdi.yingdi_delete = #{params.yingdiDelete}


          and UNIX_TIMESTAMP(yingdi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yingdi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
